package cn.wolfcode.edu.web.controller;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;/** * Created by felix on 2017/12/17. */// 主页页面@Controllerpublic class IndexController {	@RequestMapping("index")	public String index() {		return "index";	}	@RequestMapping("main")	public String main() {		return "main";	}	@RequestMapping("login")	public String login() {		return "forward:/login.jsp"; // 请求转发:登陆失败后会返回到这里,携带错误回到login界面	}	// 注销	@RequestMapping("logOut")	public String logOut() {		return "login";	}	// 注销	@RequestMapping("calendar")	public String calendar() {		return "calendar";	}}